import random

def create_poem():
    lines = []

    while True:
        line = input("Enter a line to write: ")
        try:
            line = input("Enter a line to skip: ")
            if not line or not line.strip() or len(line) != 2:
                print("Line must be valid and have at least two characters.")
                continue
            if len(line) == 1:
                lines.append(line)
        except ValueError as e:
            print(f"Error: {e}")
            break

        for char in line:
            if char not in ' ':
                print("Char not found in the line.")
                continue

            if char == 'bye':
                break

            if char == '\n':
                lines.append('')

    with open('friendship_poem', 'w') as friend_poem:
        for line in lines:
            friend_poem.write(line + '\n')
```

